import { moneyFormat } from '../helper';
import '../header.css';

function Header({ total, money }) {
  return (
    <>
      <div className="container elonrow">

        <div className="row text-center elonrow w-100">
          <div className="col-8 col-md-6 col-lg-5 mx-auto elon p-3">
            <div className="elonphoto mx-auto">
              <img src="http://ictmedia.com.tr/Media/News/9779.jpg" />

              <h1 className="spend mt-4">Spend Elon Mask's Money</h1>
            </div>
          </div>
        </div>
        </div>
        <div className="row text-center mx-auto">
          <div className="col-12 mx-auto">

          {total > 0 && money - total !== 0 && (
            <div className="header">Harcayacak <span>${moneyFormat(money - total)}</span> paranız kaldı!</div>
          )}
          {total === 0 && (
            <div className="header">Harcamak için <span>${moneyFormat(money)}</span> paranız var!</div>
          )}
          {money - total === 0 && (
            <div className="header empty">Paran bitti, parasız insan boş insandır!</div>
          )}

          </div>
        </div>



    </>
  )
}

export default Header
