const moneyFormat = (money) => {
	return money.toLocaleString()
}
export {moneyFormat}

function numFormatter(money) {
    if(money > 999 && money < 1000000){
        return (money/1000)+ 'K'; // convert to K for moneyber from > 1000 < 1 million 
    }else if(money > 1000000){
        return (money/1000000) + 'M'; // convert to M for moneyber from > 1 million 
    }else if(money > 100000000){
        return (money/100000000) + 'b';
	}else if(money < 900){
        return money; // if value < 1000, nothing to do
    }
}
export {numFormatter}
