import '../basket.css';
import BasketItem from './BasketItem';
import {moneyFormat} from '../helper';

function Basket({basket, resetBasket, total, products}) {
	return (
		<>
      <div className="container receipt-container mt-4 text-center w-100 mb-5 py-5">
        <div className="row receipt-row mx-auto text-center">
          <div className="col-md-4 mx-auto text-center">
            <h1 className="receipt mb-4">Your Receipt</h1>
            <ul>
					{basket.map(item => (
						<BasketItem key={item.id} item={item} product={products.find(p => p.id === item.id)}/>
					))}
				</ul>


          </div>
        </div>

        <div className="row">
          <div className="col-md-4">
          <div className="total">
					 <span>Toplam:</span>${moneyFormat(total)}
				   </div>
				    <button className="basket-reset-btn" onClick={resetBasket}>Sepeti Sıfırla</button>
          </div>
        </div>
      </div>
		</>
	)
}

export default Basket
